import './App.css';
import StartScreen from './components/StartScreen';
import { useCallback, useEffect, useState } from 'react';
import { wordsList } from "./data/words";
import Game from './components/Game';
import GameOver from './components/GameOver'

const stages = [
  {id: 1, name: "start"},
  {id: 2, name: "game"},      /*Array com cada estágio declarado.*/
  {id: 3, name: "end"},
]

function App() {
  const [gameStage, setGameStage] = useState(stages[0].name) /*Ele começa no estágio. Crio duas variáveis para fazer o código e a lógica.*/
  const [words] = useState(wordsList)

  const [pickedWord, setPickedWord] = useState("");
  const [pickedCategory, setPickedCategory] = useState("");
  const [letters, setLetters] = useState([]);

  const pickWordAndCategory = () => {
    const categories = Object.keys(words);

    const category = categories[Math.floor(Math.random() * Object.keys(categories).length)]
    console.log(category)

    const word = words[category][Math.floor(Math.random() *  words[category].length)]

    console.log(word)


    return{word,category}
  }



  const startGame = () => {
    const {word, category} = pickWordAndCategory();

    let wordLetters = word.split(""); // 

    // Deixar as letras todas minúsculas
    wordLetters = wordLetters.map((l) => l.toLowerCase());

    console.log(word, category);
    console.log(wordLetters);

    setGameStage(stages[1].name)  /*Quando a pessoa aperta no botão ele passa para o estágio 1.*/
  }

  const verifyLetter = () => {
    setGameStage(stages[2].name)   /*Verificando as letras do input*/
  }

  const retry = () => {
    setGameStage(stages[0].name) /*Função para resetar o jogo*/
  }

  return (
    <div className="App">
      {gameStage === "start" && <StartScreen startGame={startGame} />}
      {gameStage === "game" && <Game verifyLetter={verifyLetter}/>}
      {gameStage === "end" && <GameOver retry={retry}/>}
    </div>
  );
}

export default App;
